class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = arr.size(); 

        for(int i = 0;i<k-1;i++){
            arr.push_back(arr[i]);
        }

        int ans = 0,cnt = 1;
        for(int i = 1;i<arr.size();i++){
            if(arr[i] != arr[i-1]){
                cnt += 1;
            }
            else{
                cnt = 1;
            }

            if(cnt >= k){
                ans += 1;
            }
        }

        return ans;
    }
};
