class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = arr.size();

        for(int i = 0; i < n; ++i)   arr.push_back(arr[i]);

        int l = 0;
        int r = 1;
        int curr = 1;
        int cnt = 0;


        while(l < n)
        {
            while(r - l + 1 > k)
            {
                l++;
                curr--;
            }

            if(l >= n)
                break;

            if(arr[r] != arr[r-1])
            {
                curr++;
            }
            else 
            {
                curr = 1;
                l = r;
            }

            if(curr == k)
                cnt++;

            r++;
        }

        return cnt;
    }
};
