class Solution {
     public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int[] arr = new int[n << 1];
        for (int i = 0; i < n; i++) {
            arr[i] = arr[i + n] = colors[i];
        }
        int ans = 0;
        for (int i = 0, j = i; i < arr.length; i = j) {
            j++;
            while (j < arr.length && arr[j - 1] != arr[j]) {
                j++;
            }
            int r = Math.min(j - 1, n + k - 2);
            if (i < n && r - i + 1 >= k) {
                ans += r - i + 1 - k + 1;
            }
        }
        return ans;
    }
}
