class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size(); 
        int cnt=0; 
        if(k==1)return n; 
        int altlen=1; 
        int i=1; 
        while(i<n+k-1){ 
            int cent=i; 
            int prev=i-1; 
            if(cent>=n) cent-=n;
            if(prev>=n) prev-=n;
            if(colors[cent]!=colors[prev]) ++altlen;
            else altlen=1;
            if(altlen>=k) { 
                ++cnt; 
            } 
            ++i; 
        } 
        return cnt; 
    }
};
