class Solution:
    def numberOfAlternatingGroups(self, a: List[int], k: int) -> int:
        n = len(a)
        ans = 0
        a *= 2
        l = 0
        for i in range(n + k - 1):
            if i == 0 or a[i] == a[i - 1]:
                l = 1
            else:
                l += 1
            if l >= k:
                ans += 1
        return ans
