class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size(), cur = 0, ans=0;
        for(int i=0;i<k-1;++i) c.push_back(c[i]);
        n += k-1;
        for(int i=1;i<n;++i){
            if(!cur && c[i]!=c[i-1]) cur = 2;
            else if(c[i]!=c[i-1]) cur++;
            else cur = 0;
            if(cur>=k) ans++;
            cout<<c[i]<<' ';
        }
        return ans;
    }
};
