class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        //using sliding window
        vector<int> temp;
        for(int i=0; i<k-1; i++){
            temp.push_back(colors[i]);
        }
        for(auto it: temp){
            colors.push_back(it);
        }

        int n = colors.size();
        int i=0, j=1;
        int cnt = 0;
        while(j < n){
            if(colors[j] != colors[j-1]){
                if(j - i + 1 == k){
                    cnt++;
                    i++;
                }
                j++;
            }
            else{
                i = j;
                j++;
                continue;
            }

        }

        return cnt;
    }
};
