class Solution {
public:

    bool isPalindrome(string s){
        int n = s.size();
        for(int i=0; i<n/2; i++){
            if(s[i] != s[n-i-1]){
                return false;
            }
        }
        return true;
    }

    bool isPrime(int n){
        for(int i=2; i*i<=n; i++){
            if(n % i == 0){
                return false;
            }
        }
        return true;
    } 

    void updateAns(vector<int> &colors, int k, int &ans){
        int n = colors.size();
        unordered_map<string,int> mp;
        vector<int> arr(n+1);
        for(int i=0; i<=n; i++){
            arr[i] = 0;
        }
        int i=1;
        while(i < n){
            arr[i] += arr[i-1] + abs(colors[i] - colors[i-1]);
            i++;
        }
        k = k - 1;
        i = k+1;
        while(i < n){
            if(arr[i] - arr[i-k] == k){
                ans++;
            }
            i++;
        }
    }

    int getAns(vector<int> &colors, int k){
        colors.insert(colors.end(), colors.begin(), colors.begin()+k);

        int n = colors.size();
        int ans = 0;

        updateAns(colors, k, ans);

        return ans;
    }

    int numberOfAlternatingGroups(vector<int> &colors, int k) {
        int ans = getAns(colors, k);
        return ans;
    }
};

