class Solution {
public:
int numberOfAlternatingGroups(vector<int>& colors, int k) {
    ll ans = 0;
    loop(i ,0 , k-2){
        colors.push_back(colors[i]);
    }
    ll n = colors.size();
    colors.push_back(-1);
    ll l = 0 , r = 0;
    while(r<n){
        if(r - l + 1 == k){
            ans++;
            l++;
            continue;
        }
        if(colors[r] != colors[r+1]){
            r++;
            continue;
        }else{
            l = r+1;
            r = r+1;
            continue;
        }
    }
    return (int)ans;
}

