class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {


        vector<int>c=colors;

            for(auto x:colors)
                c.push_back(x);


        int n=colors.size();

        vector<int>p(2*n);

        for(int i=0;i<2*n;i++){
            if(c[i]==0)
                c[i]=-1;
        }

        p[0]=c[0];

        for(int i=1;i<2*n;i++)
            p[i]=p[i-1]+c[i];

        multiset<int>st;

        int ans=0;

        for(int i=0;i<k;i++){
            st.insert(p[i]);
        }

        int mn=*st.begin();
        int mx=*(--st.end());

        if((mx-mn)<=1){

        if(c[0]==-1){
            if(mn==p[0])
                ans++;
        }
        else{
            if(mx==p[0])
                ans++;
        }
        }

        for(int i=1;i<n;i++){
            st.erase(st.find(p[i-1]));
            st.insert(p[i+k-1]);

            int mn=*st.begin();
        int mx=*(--st.end());

            if(mx-mn>1)
                continue;

            if(c[i]==-1){
                if(mn==p[i])
                    ans++;
            }
            else{
                if(mx==p[i])
                    ans++;
            }
        }

        return ans;
    }
};
