class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i=0; i<k-1; i++) colors.push_back(colors[i]);
        int l = 0, r = 1;
        n = colors.size();
        int res = 0;
        while(r<n){
            if(colors[r]==colors[r-1]){
                l = r;
                r++;
                continue;
            }
            if(r-l+1>=k) res++;
            r++;
        }
        return res;
    }
};
