class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {

        int n = a.size();
        queue<int>q;
        int ind = 0;
        int ans = 0;
        for(int i=0;i<n;i++)
        {
            if(q.size() == k)
            {
                q.pop();
            }

            while(q.size()!=k)
            {
                if(q.size() && a[ind] == q.back())
                {
                    break;
                }
                q.push(a[ind]);
                ind++;
                ind%=n;
            }

            if(q.size() != k)
            {
                while(q.size())
                q.pop();

                if(ind<=i)
                break;

                i = ind;
                --i;
            }
            else
            {
                ans++;
            }
        }

        return ans;
    }
};
