class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> a(n, 0);
        for (int i = 0; i < n; i++) {
            if (colors[i] != colors[(i + 1) % n]) a[i] = 1;
        }

        int ans = 0;
        int curr = 0;
        for (int i = 0; i < k - 1; i++) curr += a[i];
        for (int i = 0; i < n; i++) {
            if (curr == k - 1) ans++;
            curr -= a[i];
            curr += a[(k - 1 + i) % n];
        }
        return ans;
    }
};
