class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> arr;
        int last= colors[0];
        int curr=1;
        bool flag=false;
        for(int i=1;i<colors.size();i++){
            if(colors[i]==last){
                arr.push_back(curr);
                curr=1;
                flag=true;
            }else{
                curr++;
            }
            last=colors[i];
        }
        arr.push_back(curr);
        // cout<<arr.size()<<endl;
        // for(int i=0;i<arr.size();i++){
        //     cout<<arr[i]<<" ";
        // }
        // cout<<endl;
        if(arr.size()==1 && arr[0]==colors.size() && colors[0]!=colors.back() && !flag){
            return colors.size();
        }
        if(colors[0]!=colors.back() && arr.size()>1){
            int a= arr.back();
            arr.pop_back();
            arr[0]+=a;
        }
        long long ans=0;

        for(int i=0;i<arr.size();i++){
            //cout<<arr[i]<<endl;
            ans+=max(0,arr[i]-(k-1));
        }
        return ans;
    }
};
