class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        for(int i = 0 ; i < n; i ++) a.push_back(a[i]);
        for(int i = 2 * n - 1; i > 0; i --){
            a[i] = (a[i] != a[i - 1]) ;
        }

        // for(auto i: a) cout << i << ' ' ;
        // cout << endl;

        for(int i = 1; i < 2 * n ; i ++){
            a[i] += a[i - 1];
        }
        int ans = 0 ;
        for(int i = k - 1 , j = -1; j + 1 < n ; i ++, j ++ ){
            int add = (a[i] - a[j + 1]) == k - 1 ;  
            ans += add ;
            // cout << j << ' ' << i << "= " << add << endl;
        }

        return ans;
    }
};
