class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        vector<int> dp(n, 0);
        int s = 1;
        for(int i = 0; i < 3 * n; i++){
            int cur = i % n, prev = (i - 1 + n) % n;
            if(a[cur] == a[prev]){
                s = 1;
            }
            else {
                s++;
            }
            dp[cur] = max(dp[cur], s);
        }
        int ans = 0;
        for(int i = 0; i < n; i++){
            if(dp[i] >= k)ans++;
        }        
        return ans;
    }
};
