class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        vector<int> v(n,0);

        for(int i=0;i<n;i++){
            if(colors[i%n] != colors[(i+1)%n]){
                v[i] = 1;
            }
        }

        // for(auto it : v){
        //     cout<<it<<" ";
        // }
        // cout<<endl;

        int i=0,j=k-2;
        int counter = 0;
        for(int x=0;x<k-1;x++){
            if(v[x] == 1) { counter++; }
        }
        int ans = 0;
        for(i=0;i<n;i++){
            // cout<<counter<<endl;
            if(counter == k-1){
                ans++;
            }
            counter -= v[i];
            j = (j+1)%n;
            counter += v[j];
        }

        return ans;
    }
};
