class Solution:
    def numberOfAlternatingGroups(self, l: List[int], k: int) -> int:
        l.extend(l[:k-1])
        n = len(l)

        fl = 1
        dp = [1 for i in range(n)] # dp[i] is the longest alt seq ending at ind i

        ans = 0
        for i in range(1,n):
            if l[i] == l[i-1]:
                dp[i] = 1
            else:
                dp[i] = dp[i-1]+1
            ans += (dp[i]>=k)

        # print(dp)
        return ans
