class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int count = 0;
        vector<int> eColors = colors;

        eColors.insert(eColors.end(), colors.begin(), colors.begin() + k - 1);

        bool alter = true;
        for (int i = 0; i < k - 1; ++i) {
            if (eColors[i] == eColors[i + 1]) {
                alter = false;
                break;
            }
        }

        if (alter) count++;
        int i=1;
        while(i<n) {
            if (eColors[i + k - 2] == eColors[i + k - 1]) {
                alter = false;
            }
            if (eColors[i - 1] == eColors[i]) {
                alter = true;
                for (int j = i; j < i + k - 1; ++j) {
                    if (eColors[j] == eColors[j + 1]) {
                        alter = false;
                        break;
                    }
                }
            }
            if (alter) count++;
            i++;
        }

        return count;
    }
};
