public class Solution {
    public int NumberOfAlternatingGroups(int[] colors, int k)
    {
        var result = 0;

        var previous = colors[0];

        var length = 1;

        for (int i = 1; i < colors.Length; i++)
        {
            if (colors[i] == previous)
            {
                if (length >= k)
                {
                    result += length - k + 1;
                }

                length = 0;
            }

            length++;
            previous = colors[i];
        }

        for (int i = 0; i < k; i++)
        {
            if (i == k - 1 || colors[i] == previous)
            {
                if (length >= k)
                {
                    result += length - k + 1;
                }

                break;
            }

            length++;
            previous = colors[i];
        }

        return result;
    }
}
