class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {
        int n=nums.size();
        for(int i=0;i<n;i++){
            nums.push_back(nums[i]);
        }
        vector<int> pre;
        pre.push_back(0);
        for(int i=1;i<nums.size();i++){
            if(nums[i]!=nums[i-1]){
                pre.push_back(1);
            }
            else pre.push_back(0);
        }
        for(int i=1;i<pre.size();i++){
            pre[i]+=pre[i-1];
        }
        int ans=0;
        set<pair<int,int>> st;
        for(int i=k-1;i<pre.size();i++){
            if(pre[i]-pre[i-k+1]==k-1){
                int l=(i-k+1)%n;
                int r=i%n;
                if(!st.count({l,r})){
                    st.insert({l,r});
                    ans++;
                }
            }
        }
        return ans;
    }
};
