class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors += colors
        cnt = 0
        for i in range(k-2):
            cnt += colors[i] != colors[i+1]
        ans = 0
        for i in range(k-2, n+k-2):
            cnt += colors[i] != colors[i+1]
            ans += cnt == k-1
            cnt -= colors[i-k+2] != colors[i-k+3]
        return ans
