class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        for(int i=0;i<k-1;i++){
        colors.push_back(colors[i]);
        }
         int ans=0;
        n=colors.size();
         vector<int>prefix(n,1);
         for(int i=1;i<n;i++){
           if(colors[i-1]!=colors[i]) prefix[i]=prefix[i-1]+1;
            else prefix[i]=1;
         }
          for(int i=0;i<n;i++){
           if(prefix[i]>=k) ans++;
          }

        return ans;
    }
};
