import java.util.*;

class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int n = colors.length;
        int[] a = new int[n * 2];
        for(int i = 0; i < n; i++) {
            a[i] = colors[i];
        }
        for(int i = n; i < n * 2; i++) {
            a[i] = colors[i - n];
        }
        int ans = 0, len = 0;
        for(int i = 0; i < n - 1 + k; i++) {
            if(i == 0 || a[i] != a[i - 1]) {
                len++;
            }
            else {
                ans += Math.max(0, len - k + 1);
                len = 1;
            }
        }
        ans += Math.max(0, len - k + 1);
        return ans;
    }
}
