class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        cnt = 0
        i = 0
        res = 0
        flag = True
        for j in range(n):
            if colors[j % n] == colors[(j + 1) % n]:
                flag = False
                i = j + 1
                break
        if flag:
            return n
        while i < n + j or cnt != 0:
            if colors[i % n] != colors[(i + 1) % n]:
                cnt += 1
            else:
                cnt = 0
            if cnt == k-1:
                cnt -= 1
                res += 1
            i += 1 
        return res
