class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int count=0;
        if(k==1)
        {
            return n;
        }
        int alternate=1;
        int i=1;
        while(i<n+k-1)
        {
            int curr=i;
            int prev=i-1;

            if(curr>=n)
            {
                curr-=n;
            }
            if(prev>=n)
            {
                prev-=n;
            }
            if(colors[curr]!=colors[prev])
            {
                alternate++;
            }
            else
            {
                alternate=1;
            }
            if(alternate>=k)
            {
                count++;
            }
            ++i;
        }
        return count;

    }
};
