typedef long long int ll;
class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        // only odd blue even red 
        ll ans = 0; ll n = c.size();
        ll mid = 0;
        vector<ll>v;
        for(int i=0; i<k; i++){
            v.push_back(c[i]);
        }
        ll chk1 = 1, chk2 = v.size()-1;
        for(int i=1; i<(k-1); i++){
            if((v[i] != v[i-1]) && (v[i] != v[i+1])){
                mid++;
            }
        }

        if(mid == (k-2)){
            ans++;
        }
        for(int i=1; i<n; i++){
            ll nxt = (k-1+i)%n;
            v.push_back(c[nxt]);
            if((v[chk1] != v[chk1-1]) && (v[chk1] != v[chk1+1])){
                mid--;
            }
            if((v[chk2] != v[chk2-1]) && (v[chk2] != v[chk2+1])){
                mid++;
            }
            //cout<<mid<<endl;
            if(mid == (k-2)){
                ans++;
            }
            chk1++; chk2++;
        }
        return ans;
    }
};
