class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
      int n=colors.size();
        int cou=0;

        auto isDifferent=[&](int a, int b) {
            return colors[a%n]!=colors[b%n];
        };

        int alterPai=0;

        for (int j=0;j<k-1;++j) {
            if (isDifferent(j,j+1)) {
                alterPai++;
            }
        }

        if (alterPai==k-1) {
            cou++;
        }

        for (int i=1;i<n;++i) {
            if (isDifferent(i-1,i)) {
                alterPai--;
            }
            if (isDifferent(i+k-2,i+k-1)) {
                alterPai++;
            }
            if (alterPai==k-1) {
                cou++;
            }
        }

        return cou;
    }
};
