func numberOfAlternatingGroups(colors []int, k int) int {
	extendedColors := append(colors, colors[:k-1]...)
	result := 0
	currentGroupLength := 1

	for i := 1; i < len(extendedColors); i++ {
		if extendedColors[i] != extendedColors[i-1] {
			currentGroupLength++
		} else {
			currentGroupLength = 1
		}

		if currentGroupLength >= k {
			result++
		}
	}

	return result
}
