class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
      int prev=-1;
      for(int i=0; i<k-1; i++) colors.push_back(colors[i]);
      int n=colors.size();
      for(int i=1; i<k; i++){
        if(colors[i]==colors[i-1]) prev=i-1;
      }
      int ans=0;
      if(prev==-1) ans++;
      for(int i=k; i<n; i++){
        if(colors[i]==colors[i-1]) prev=i-1;
        if(prev<=i-k) ans++;
      }
      return ans;
    }
};
