class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n=colors.size();
        int ans=0;
        for(int i=0;i<k-1;i++){
        colors.push_back(colors[i]);
        }
        int count=1;
        for(int i=0;i<n+k-2;i++){
            if(colors[i]!=colors[i+1]){
                count++;
                if(count==k){ans++;count--;}
            }else{
                count=1;
            }
        }
        return ans;
    }
};
