class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
        int n = arr.size();
        for (int i = 0; i < k; ++i) arr.push_back(arr[i]);

        int count = 0;
        int pos = 0;
        for (int i = 0; i < n + k - 1; ++i) {
            if (i == 0 || arr[i] != arr[i - 1]) {
                // cout << i << " " << pos << endl;
                if (i - pos + 1 >= k) count++;
            }
            else {
                pos = i;
            }
        }
        return count;
    }
};
