class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();

        for(int i= 0; i< n; i++)
        {
            c.push_back(c[i]);
        }

        int l= 0;
        int r= 1;
        int ans = 0;

        while( r < n + k - 1)
        {
            if( c[r] == c[r-1])
            {
                l = r;
            }

            if( r- l + 1 == k)
            {
                ans++;
                l++;
            }
            r++;
        }

        return ans;

    }
};
