class Solution:
    def numberOfAlternatingGroups(self, A, k: int) -> int:
        n = len(A)
        A = A + A
        res = 0
        dp = [1] * (2 * n)
        for i in range(1, 2 * n):
            if A[i] != A[i - 1]:
                dp[i] = dp[i - 1] + 1
        for i in range(n + k - 1):
            res += dp[i] >= k

        return res
