class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>arr;
        for(auto &it:colors) arr.push_back(it);
        for(int l=0;l<k-1;l++) arr.push_back(colors[l]);

        int res=0;

        for(int i=0;i<arr.size();i++)
        {
            int c=1;
            for(int j=i+1;j<arr.size();j++)
            {
                if(arr[j] != arr[j-1]) c++;
                else
                {
                     i=j-1;
                    break;
                }   
                if(j== arr.size()-1)
                {
                    i=3*arr.size();
                    break;
                }
            }
            res += max(0,c-k+1);
        }
        return res;
    }
};
