class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=(int)a.size();
        vector<int> v;
        for(int i=0;i<n;i++) v.push_back(a[i]);
        for(int i=0;i<n;i++) v.push_back(a[i]);
        for(int i=2;i<2*n;i+=2) v[i]+=v[i-2];
        for(int i=3;i<2*n;i+=2) v[i]+=v[i-2];
        int ans=0;
        for(int i=0;i<n;i++){
            int k1=(k+1)/2,k2=(k/2);
            int res1=v[i+(k1-1)*2];
            if(i-2>=0) res1-=v[i-2];
            int res2=v[i+1+(k2-1)*2];
            if(i-1>=0) res2-=v[i-1];
            if(a[i]==0 && res1==0 && res2==k2) ans++;
            else if(a[i]==1 && res1==k1 && res2==0) ans++;
        }
        return ans;
    }
};
