#define all(a) a.begin(), a.end()

class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        a.insert(a.end(), all(a));
        int ans = 0;
        for (int i = 0, j = 1, c = 0; i < n; i ++) {
            while (j <= i + k - 1) {
                c += a[j - 1] == a[j];
                j ++;
            }
            ans += c == 0;
            c -= a[i] == a[i + 1];
        }
        return ans;
    }
};
