class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n = a.size();
        set<int> s;
        for (int i = 0; i < n - 1; i++) {
            if (a[i] == a[i + 1]) {
                s.insert(i);
            }
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            auto it = s.lower_bound(i);
            if (it != s.end() && *it <= i + k - 2) {
                continue;
            }
            if (i + k - 1 >= n) {
                if (a[n - 1] == a[0]) {
                    continue;
                }
                auto it = s.begin();
                if (it != s.end() && *it <= i + k - 1 - n - 1) {
                    continue;
                }
            }
            ans++;
        }
        return ans;
    }
};
