class Solution {
public:
        int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
        vector<int> t = c;

        for (int i = 0; i < k - 1; i++) {
            t.push_back(c[i]);
        }

        int l = 0, r = 0;
        int m = t.size();
        int p = -1;
        int a = 0;

        for (int i = 0; i < m; i++) {
            if (p != t[i]) {
                p = t[i];
                if (i - l + 1 == k) {
                    a += 1;
                } else if (i - l + 1 > k) {
                    if (k % 2 == 0 && t[l + 1] != t[i]) {
                        a += 1;
                        l++;
                    } else if (k % 2 == 1 && t[l + 1] == t[i]) {
                        a += 1;
                        l++;
                    }
                }
            } else {
                l = i;
            }
        }

        return a;
    }
};
