class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        d = deque()
        for i in range(-k, 0, 1):
            if colors[(i) % len(colors)] == colors[(i - 1) % len(colors)]:
                d.append(i)
        res = 0
        for i in range(len(colors) + k):
            if colors[(i) % len(colors)] == colors[(i - 1) % len(colors)]:
                d.append(i)
            while d and i - d[0] >= k - 1: d.popleft()
            # print(i, d)
            if i >= k: res += not d
        return res
