class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int valen = colors.length;
        int cntt = 0;
        if (k == 1) {    
            return valen;
        }
        int alterle = 1;
        int a = 1;
        while (a < valen + k - 1) {
            int cu = a;
            int pr = a - 1;
            if (cu >= valen) {
                cu -= valen;
            }
            if (pr >= valen) {
                pr -= valen;
            }
            if (colors[cu]!=colors[pr]) {
                ++alterle;
            } else {
                alterle = 1;
            }
            if (alterle >= k) {
                ++cntt;
            }
            ++a;
        }
        return cntt;

    }
}
