class Solution:
    def numberOfAlternatingGroups(self, a: List[int], k: int) -> int:
        res = 0
        n = len(a)
        # f[i]: 以i为右端点的交替组的数目
        a = a + a
        f = [0] * (n + k)
        for i in range(n + k):
            if a[i] == a[i - 1]:
                f[i] = 1
            else:
                f[i] = f[i - 1] + 1
        #print(f)
        for i in range(n + k):
            if f[i] >= k:
                if i >= n and f[i - n] >= k:
                    continue
                res += 1
        return res
