class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int idx = 0;
        for(int i=1; i<k; i++) 
            colors.push_back(colors[idx++]);

        int res = 0;
        int curr = 1;

        for(int i=1; i<colors.size(); i++){

            curr = (colors[i] != colors[i-1] ? curr+1 : 1);
            if(curr >= k) res++;
        }
        return res;
    }
};
