class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        const int n = a.size();
        vector<int> b = a;
        for (int x : a) {
            b.push_back(x);
        }
        int m = 2 * n;
        vector<int> dp(m, 1);
        for (int i = 1; i < m; ++i) {
            if (b[i] != b[i-1]) {
                dp[i] = dp[i-1] + 1;
            }
        }

        int ans = 0;
        for (int i = k - 1; i < k + n - 1; ++i) {
            if (dp[i] >= k) {
                ans += 1;
            }
        }

        return ans;
    }
};
