class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        vector<int> v;
        for(auto it : c) v.push_back(it);
        for(int i=0;i<k-1;i++) v.push_back(c[i]);
        int ans = 0;
        for(int i=0;i<v.size();i++) {
            int ct = 1;
            for(int j=i+1;j<v.size();j++) {
                if(v[j] != v[j - 1]) ct++;
                else {
                    i = j - 1;
                    break;
                }
                if(j == v.size() - 1) {
                    i = v.size()+2;
                    break;
                }
            }
            if(ct-k+1>0) ans+=(ct-k+1);
        }
        return ans;
    }
};
