class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        ans = 0
        n = len(colors)
        colors *= 2
        l = n - k + 1
        for i in range(n - k + 1, n * 2):
            if i > 0 and colors[i] == colors[i - 1]:
                l = i
            if i - l + 1 >= k and i >= n - k + 1:
                ans += 1
        return ans
