class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        colors += colors[:k - 1]
        res = 0
        l = 0
        for r in range(len(colors)):
            if r == 0 or colors[r - 1] != colors[r]:
                pass
            else:
                l = r
            # print(k, l, r, colors)
            if (r - l + 1) >= k:
                res += 1
        return res
