class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        for(int i=0;i<k-1;i++)
            colors.push_back(colors[i]);
        int ans=0;
        int j=0;
        for(int i=1;i<colors.size();i++)
        {
            if(colors[i] != colors[i-1])
            {
                if((i-j+1) == k)
                {
                    ans++;
                    j++;
                }
            }
            else
                j=i;
        }
        return ans;
    }
};
