class Solution {
    public int func(int colors[], int k) {
        int len = colors.length;
        int p = 0;
        int c1 = colors[0];
        int[] pr = new int[len + 1];
        Arrays.fill(pr, Integer.MAX_VALUE);
        pr[0] = 0;

        for (int i = 1; i < len; i++) {
            if (c1 != colors[i]) {
                pr[i] = p;
            } else {
                pr[i] = i;
                p = i;
            }
            c1 = colors[i];
        }

        int res = 0;

        for (int i = 0; i < len; i++) {
            if (i + k - 1 > len - 1) {
                int ex = k - (len - i) - 1;
                if (pr[ex] == 0 && colors[0] != colors[len - 1]) {
                    if (pr[len - 1] <= i) {
                        res++;
                    }
                } else {
                    break;
                }
            } else {
                if (pr[i + k - 1] <= i) {
                    res++;
                }
            }
        }

        return res;

    }

    public int numberOfAlternatingGroups(int[] colors, int k) {

