class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n=v.size();
        for(int i=0;i<n;i++){
            v.push_back(v[i]);
        }
        int ans=0;
        int i=0;
        int j;
        while(i<n){
            j=i;
            while(i<n+k-1 && i<v.size()-1 && v[i]!=v[i+1]){
                i+=1;
            }
            // dbg(j);
            // dbg(i);
            // cerr<<(i-j+1-k+1)<<enl;
            i=min(i,n+k-2);
            ans+=max(0,i-j+1-k+1);
            i+=1;
        }
        return ans;
    }
};
